def get_inverse_captcha_part1(digit_sequence: str) -> int:
    result = 0

    for current_digit, next_digit in zip(digit_sequence[:-1], digit_sequence[1:]):
        if current_digit == next_digit:
            result += int(current_digit)
    
    if digit_sequence[0] == digit_sequence[-1]:
        result += int(digit_sequence[0])

    return result

assert get_inverse_captcha_part1("1122") == 3
assert get_inverse_captcha_part1("1111") == 4
assert get_inverse_captcha_part1("1234") == 0
assert get_inverse_captcha_part1("91212129") == 9


def get_inverse_captcha_part2_01(digit_sequence: str) -> int:
    seq_length = len(digit_sequence)
    step = seq_length // 2
    result = 0

    for current_idx in range(0, seq_length):
        next_idx = (current_idx + step) % seq_length
        if digit_sequence[current_idx] == digit_sequence[next_idx]:
            result += int(digit_sequence[current_idx])

    return result

def get_inverse_captcha_part2_final(digit_sequence: str) -> int:
    seq_length = len(digit_sequence)
    step = seq_length // 2
    result = 0
    
    for current_digit, next_digit in zip(digit_sequence, digit_sequence[step:]):
        if current_digit == next_digit:
            result += 2 * int(current_digit)
            
    return result

assert get_inverse_captcha_part2_final("1212") == 6
assert get_inverse_captcha_part2_final("1221") == 0
assert get_inverse_captcha_part2_final("123425") == 4
assert get_inverse_captcha_part2_final("123123") == 12
assert get_inverse_captcha_part2_final("12131415") == 4

PUZZLE_INPUT = ""

if __name__ == "__main__":
    result1 =  get_inverse_captcha_part1(PUZZLE_INPUT)
    print(f"result1 = {result1}")
    assert result1 == 1044

    result2 =  get_inverse_captcha_part2_final(PUZZLE_INPUT)
    print(f"result2 = {result2}")
    assert result2 == 1054

    print("End of main()")